    public InstructionTextPanel() {
        // layout
        setLayout(new BorderLayout());
        
        // text
        JLabel label = new JLabel("<html><body style='width: 1000px'>" + instruction + "</body></html>");
        label.setHorizontalAlignment(SwingConstants.LEFT);
        label.setVerticalAlignment(SwingConstants.TOP);
        label.setFont(new Font("Segoe UI", Font.BOLD, 18));

        // centering panel
        JPanel centeringPanel = new JPanel();
        centeringPanel.setBackground(new Color(205, 221, 237));
        centeringPanel.add(label);

        // scroll panel
        JScrollPane scrollPane = new JScrollPane(centeringPanel);
        scrollPane.setBorder(null);
        scrollPane.getVerticalScrollBar().setUnitIncrement(18);

        // add
        add(scrollPane, BorderLayout.CENTER);
    }
